INSERT INTO `proprietarychinesemedicines` VALUES ('30001', '一捻金', 'Yinianjin', '暂无', '大黄100g 炒牵牛子200g 槟榔100g 人参100g  朱砂30g', '以上五味，朱砂水飞成极细粉；其余大黄等四味粉碎成细粉，与上述粉末配研，过筛，混匀，即得。', '本品为黄棕色至黄褐色的粉末；气微，味微苦、涩。', '消食导滞，祛痰通便。用于脾胃不利、痰食阻滞所致的积滞，症见消食停乳、腹胀便秘、痰盂喘咳。', '口服。一岁以内一次0.3g，一岁至三岁一次0.6g，四岁至六岁一次1g，一日1~2次；或遵医嘱。', '每袋装1.2g。', '密封。', '无。', '3');
INSERT INTO `proprietarychinesemedicines` VALUES ('30002', '一清胶囊', 'Yiqing Jiaonang', '暂无', '黄连660g、大黄2000g、黄芩1000g', '以上三味，分别加水煎煮两次，第一次1.5小时，第二次1小时，合并煎液，滤过，滤液分别减压浓缩，', '本品为硬胶囊，内容物为浅黄色至黄棕色的粉末，气微，味苦。', '清热泻火解毒，化瘀凉血止血。用于火毒热所致的身热烦躁、目赤口疮、咽喉牙龈肿痛、大便秘结、吐血、咯血、', '口服。一次2粒，一日3次。', '每粒装0.5g。', '密封。', '出现腹泻时，可酌情减量。', '3');
INSERT INTO `proprietarychinesemedicines` VALUES ('30003', '一清颗粒', 'Yiqing Keli', '暂无', '黄连165g 大黄500g 黄芩250g', '以上三味，分别别加水煎煮二次，第一次1.5小时，第二次1小时，合并煎液，滤过，滤液减压浓缩至相对密度约为1.25（70℃），', '本品为黄褐色的颗粒；味微甜、苦。', '清热泻火解毒，化瘀凉血止血。用于火毒血所致的身热烦躁、目赤口疮、咽喉牙龈肿痛、大便秘结、吐血、咯血、衄血、痔血；咽炎、扁桃体炎、', '开水冲服。一次7.5g，一日3~4次。', '每袋装7.5g。', '密封。', '出现腹泻时 ，可酌情减量。', '3');
INSERT INTO `proprietarychinesemedicines` VALUES ('30004', '乙肝宁颗粒', 'Yiganning Keli', '暂无', '黄芪606g 白花蛇舌草408g 茵陈606g 金钱草408g 党参490g 蒲公英408g 制何首乌490g 牡丹皮408g 丹参490g 茯苓408g', '以上十三味，黄芪加水煎煮二次，每次2小时，合并煎液，滤过，滤液浓缩至适量；茵陈提取挥发油，用倍他环糊精290g包', '本品为黄棕色至棕褐色的颗粒；味甜、微苦，或味甘、微苦（含乳糖）。', '补气健脾，活血化瘀，清热解毒。用于慢性肝炎属脾气虚弱、血瘀阻络、湿热毒蕴证，症见胁痛、腹胀、乏力、尿黄；对急性肝炎属上述', '口服。一日3次，一次1袋；儿童酌减。治疗慢性肝炎者以3个月为一个疗程。', '（1）每袋装17g（2）每袋装3g（含乳糖）。', '密封。', '服药期间忌食油腻、辛辣食物。', '3');
INSERT INTO `proprietarychinesemedicines` VALUES ('30005', '乙肝养阴活血颗粒', 'Yigan Yangyin Huoxue Keli', '暂无', '地黄 北沙参 麦冬 酒女贞子 五味子 黄芪 当归 制何首乌 白芍 阿胶珠 泽兰 牡蛎 橘红 丹参 川楝子 黄精（蒸）', '以上十六味，北沙参、白芍粉碎，过筛，取北沙参细粉67g，混匀，备用；余下粗粉备用。阿胶珠粉碎成细粉。牡蛎粉碎，', '本品为浅棕色至浅棕褐色的颗粒；味甜、微苦；或味微甜、微苦（无蔗糖）。', '滋补肝肾，活血化瘀。用于肝肾阴虚型慢性肝炎，症见面色晦暗、头晕耳鸣、五心烦热、腰腿酸软、齿鼻衄血、胁下痞块、', '开水冲服，一次20g或一次10g（无蔗糖），一日3次。', '每袋装10g。', '密封，置干燥处。', '忌烟、酒、油腻；肝胆湿热，脾虚气滞者忌用。', '3');
